import React, {Component} from 'react';
import './App.css';

import Navigation from './components/navigation/Navigation';
import Logo from './components/Logos/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import Rank from './components/Rank/Rank'
import FaceRecognition from './components/FaceRecognition/FaceRecognition'

import Particles from 'react-particles-js';
import Clarifai from 'clarifai';

const app = new Clarifai.App({
 apiKey: 'cf115bef7f3d43a4b5d793bb338e7d26'
});

const particleOptions = {
      particles: {
        number:{
          value: 100,
          density: {
            enable: true,
            value_area: 1000
          }
        }
}
}

class App extends Component{
  constructor(){
    super();
    this.state = {
      input:'',
      imageUrl: '',
      box: {}
    }
  }

  calculateFaceLocation = (data) => {
    const faceBox = data.outputs[0]
                    .data.regions[0]
                    .region_info
                    .bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: (faceBox.left_col)*100,
      topRow: (faceBox.top_row)*100,
      rightCol: (1 - (faceBox.right_col))*100,
      bottomRow: (1 - (faceBox.bottom_row))*100 
    }

  }

  displayFaceBox = (box) => {
    this.setState({ box: box })
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value })
  }

  onButtonSubmit = () => {
    this.setState( {imageUrl: this.state.input} );
    app.models.predict(
      Clarifai.FACE_DETECT_MODEL, 
               this.state.input)
      .then( response => this.displayFaceBox(
                         this.calculateFaceLocation(response)))
      .catch( err => console.log(err));
  }

  render() {
    return(
        <div className="App">
       <Particles 
          className= 'particles'
          params={particleOptions} />
          <Navigation />
          <Logo />
          <Rank />
          <ImageLinkForm 
           onInputChange = {this.onInputChange} 
           onButtonSubmit = {this.onButtonSubmit} />
          <FaceRecognition box = {this.state.box} imageUrl = {this.state.imageUrl} />
        </div>
      );
  }

}

export default App;
